import './Header.css';
import { useState, useEffect } from 'react';

    function Header({onMenuClick, onSearchbarChange}) {

        const [timer, setTimer] = useState([]);
        const [prevtext, setPrevText] = useState([]);

        const setMyTimeout = (time) => {
            const myPromise = new Promise((resolve, reject) => {setTimeout(resolve(), time);});
            return myPromise;
        };

        return (
            <header className="app-header">

                <h1>Sports Cars</h1>
                <nav className="search" onChange={e=> {
                    if (e.target.value != prevtext){
                        clearTimeout(timer);
                        //const timeoutId = setTimeout(() => onSearchbarChange(e.target.value), 1000);
                        const timeoutId = setMyTimeout(1000).then(()=> onSearchbarChange(e.target.value));
                        setPrevText(e.target.value); setTimer(timeoutId);
                    }}}>
                    <input type="text" className="form-control search-filter" placeholder="Search" />
                </nav>

            <section className="nav-items">
                <nav className="home">
                    <span>âŒ‚ Home</span>
                </nav>

            <details className="menu-top10">
            <summary>
                <nav>
                    <span>Top 10</span>
                </nav>
            </summary>

            <section>
            <nav className="top10-weight" onClick={() => { onMenuClick("weight"); }}>
                <span>Weight</span>
            </nav>
            <nav className="top10-power" onClick={() => { onMenuClick("power"); }}>
                <span>Power</span>
            </nav>
            <nav className="top10-ratio" onClick={() => { onMenuClick("ratio"); }}>
                <span>Ratio</span>
            </nav>
          </section>
        </details>
      </section>

    </header>

        );
    }

    export default Header;
