import './App.css';
import { useState, useEffect } from 'react';
import Header from './header/Header';
import User from './user/User';
import 'bootstrap/dist/css/bootstrap.css';

//https://randomuser.me/api/?results=10&gender=

function App() {

  const [data, setData] = useState({results:[]});
  useEffect(() => 
    fetch('https://randomuser.me/api/?results=10', {mode: 'cors'})
      .then(response => response.json())
      .then(json => setData(json))
    ,[]);

  let UserList = data.results.map(user => <User name={user.name.first + ' ' + user.name.last} 
    city={user.location.city} age={user.dob.age}
    phone={user.cell} email={user.email} gender={user.gender} imglink={user.picture.large}
    key={user.id.value}
  />);


  const [selctedGender, setSelectedGender] = useState(null);
  const HeaderFunction = gender => {
                                fetch('https://randomuser.me/api/?results=10&gender='+queryString(gender), {mode: 'cors'})
                                  .then(response => response.json())
                                  .then(json => setData(json));
                                setSelectedGender(gender);
  }


  return (
    <section className="app-container">
      <Header/>
      <section className="app-content">   
        {UserList}
      </section>
      <div className="overlay"></div>
    </section>
  );
}

export default App;
